    }

    Show = (e) => this.setState({name : (e.target.value)});
    hundleSubmit = (e) => {
        //e.preventDefault();
        console.log(e.target.name);
    }
    render(){
        return(
            <div className='App'>              
                <form onSubmit={this.hundleSubmit}>
                    <input type='text' onChange={this.Show}/>
                    <button>Submit</button><br/>
                </form>
                {this.state.name}
            </div>
        );
    }
}

export default App
